import React, { Component } from 'react'
/*
.eachBox{
    margin-bottom: 25px;
    border: solid black 0.5px;
    background-color: #ff4d4d;
    height: 150px;
    border-radius: 10px;
    margin-right: 20px;
}
*/
export class EachRestaurant extends Component {
    render() {
        return (
            <div style={eachBox}>
                <div style={nameAndRatingStyle}>
                    <div style={nameStyle}>
                        <h2 style={textStyle}>{this.props.store.name}</h2>
                    </div>
                    <div style={ratingStyle}>
                        {this.props.store.rating} Rating
                    </div>
                </div>

                <h3 style={textStyle}>{this.props.store.phone}</h3>
                <h3 style={textStyle}>{this.props.store.location.address1}</h3>
            </div>
        )
    }
}
const eachBox = {
    //height: '200px',
    marginLeft: '20px',
    marginTop: '35px',
    border: 'solid black 0.5px',
    backgroundColor: '#ff4d4d',
    height: '150px',
    borderRadius: '10px',
    marginRight: '20px',
    fontFamily:'\'Raleway\', sans-serif',
    overflow:'hidden',
}
const nameAndRatingStyle = {
    width: '30%',

    height: '40%',
    backgroundColor: '#green',
    display: 'flex',
    margin: '0',
    overflow: 'auto',
}

const textStyle = {
    paddingLeft: '15px',
}
const nameStyle = {
    flex: '1',
}
const ratingStyle = {
    paddingTop: '10px',
    fontSize: '25px',
    flex: '3.5',
}
export default EachRestaurant
