import React, { Component } from 'react'
import {useImage} from 'react-image'


export class EachRestaurant extends Component {
     MyImageComponent = () => {
        const {src} = useImage({
          srcList: this.props.store.image_url ,
        })

        return <img src={src} />
    }

    render() {
        return (
            <div style={eachBox}>
                <div style={nameAndRatingStyle}>
                    <div style={nameStyle}>
                        <h2 style={textStyle}>{this.props.store.name}</h2>
                    </div>
                    <div style={ratingStyle}>
                        {this.props.store.rating} Rating
                    </div>
                </div>

                <h3 style={textStyle}>{this.props.store.phone}</h3>
                <h3 style={textStyle}>{this.props.store.location.address1}</h3>
                {this.props.store.image_url}
                <div>
                    <MyImageComponent />
                </div>
            </div>
        )
    }
}

const eachBox = {
    minWidth: '600px',
    marginLeft: '20px',
    marginTop: '35px',
    border: 'solid black 0.5px',
    backgroundColor: '#FF6961',
    height: '150px',
    borderRadius: '10px',
    marginRight: '20px',
    fontFamily:'\'Raleway\', sans-serif',
}
const nameAndRatingStyle = {
    borderRadius: '10px',
    width: '100%',
    height: '40%',
    backgroundColor: '#FF6961',
    display: 'flex',
    margin: '0',
}

const textStyle = {
    paddingLeft: '15px',

}
const nameStyle = {
    borderRadius: '10px',
    flex: '1',
    backgroundColor: '#FF6961',
    minWidth: '400px',
    overflow: 'hidden',
}
const ratingStyle = {
    paddingTop: '10px',
    fontSize: '25px',
    flex: '3.5',
}
export default EachRestaurant
